var _ = require('lodash');
var app = require('./app');
var http = require('http');
var fs = require('fs');
var exports = module.exports = {};
var tcpPortUsed = require('tcp-port-used');
var colors = require('colors/safe');

exports.startServer = function (options) {
	var defaultOptions = {
		styleguidePath: 'styleguide'
	};

	options = _.assign(defaultOptions, options);

	var config = JSON.parse(fs.readFileSync('./' + options.styleguidePath + '/config.txt', 'utf8'));
	var serverInstance;

	return tcpPortUsed.check(config.serverPort)
		.then(function (inUse) {
			if (!inUse) {
				app.set('styleguideConfig', config);
				app.set('port', config.serverPort);
				serverInstance = http.createServer(app);
				serverInstance.listen(config.serverPort, function () {
					console.log(colors.green('Styleguide server listening on port ' + config.serverPort));
				}).on('error', function (error) {
					if (error.code === 'EADDRINUSE') {
						console.error(colors.red('Something went wrong and server could not start'));
					}
				});
			} else {
				console.error(colors.red('Port:' + config.serverPort + ' is already in use.'));
				console.error(colors.red('Please provide another port.'));
			}

			return serverInstance;

		});
};

var express = require('express'),
  bodyParser = require('body-parser'),

  snippets = require('./routes/snippets'),
  categories = require('./routes/categories'),
  scraper = require('./routes/scrape'),

  app = express();

//var logger = require('morgan');

process.env.NODE_TLS_REJECT_UNAUTHORIZED = "0";

//app.use(logger('dev'));
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({
  extended: false
}));

app.use(function(req, res, next) {
  if (req.headers.origin) {
    res.header('Access-Control-Allow-Origin', '*');
    res.header('Access-Control-Allow-Headers', 'X-Requested-With,Content-Type,Authorization');
    res.header('Access-Control-Allow-Methods', 'GET,PUT,PATCH,POST,DELETE');
    if (req.method === 'OPTIONS') return res.sendStatus(200);
  }
  next();
});

app.use('/snippets', snippets);
app.use('/categories', categories);
app.use('/scrape', scraper);

// catch 404 and forward to error handler
app.use(function(req, res, next) {
  var err = new Error('Not Found');
  err.status = 404;
  next(err);
});

// error handlers

// development error handler
// will print stacktrace
if (app.get('env') === 'development') {
  app.use(function(err, req, res) {
    res.status(err.status || 500);
    res.json({
      message: err.message,
      error: err
    });
  });
}

// production error handler
// no stacktraces leaked to user
app.use(function(err, req, res) {
  res.status(err.status || 500);
  res.json({
    message: err.message,
    error: {}
  });
});


module.exports = app;

var express = require('express'),
  fs = require('fs'),
  path = require('path'),
  jf = require('jsonfile'),
  helpers = require('./helpers.js'),
  router = express.Router();

router.get('/', function(req, res) {
  var allSnippets = [],
    config = req.app.get('styleguideConfig'),
    snippets,
    dataPath,
    index,
    categories = JSON.parse(fs.readFileSync(config.categories)),
    length = categories.length;

  for (index = 0; index < length; index++) {
    dataPath = path.join(config.database, categories[index].name + config.extension);

    snippets = jf.readFileSync(dataPath);
    snippets = snippets.map(helpers.mapCategory, index);
    snippets = snippets.filter(helpers.filterOutDeleted);
    allSnippets = allSnippets.concat(snippets);
  }
  res.json(allSnippets);
});

router.get('/duplicates', function(req, res) {
  var allSnippets = [],
    config = req.app.get('styleguideConfig'),
    found = false,
    dataPath,
    snippets,
    index,
    categories = JSON.parse(fs.readFileSync(config.categories)),
    length = categories.length;

  for (index = 0; index < length; index++) {
    dataPath = path.join(config.database, categories[index].name + config.extension);

    snippets = jf.readFileSync(dataPath);
    snippets = snippets.map(helpers.mapCategory, index);
    snippets = snippets.filter(helpers.filterOutDeleted);
    allSnippets = allSnippets.concat(snippets);
  }

  allSnippets.sort(helpers.duplicateComparator);

  for (index = 0, length = allSnippets.length - 1; index < length; index++) {
    if (allSnippets[index].id == allSnippets[index + 1].id) {
      res.json([allSnippets[index], allSnippets[index + 1]]);
      found = true;
      break;
    }
  }

  if (!found) {
    res.json(found);
  }
});

router.get('/category/:id', function(req, res) {
  var catId = Number(req.params.id),
    config = req.app.get('styleguideConfig'),
    dataPath,
    snippets,
    index,
    categories = JSON.parse(fs.readFileSync(config.categories)),
    length = categories.length;

  for (index = 0; index < length; index++) {
    if (categories[index].id == catId) {
      dataPath = path.join(config.database, categories[index].name + config.extension);
    }
  }

  if (!dataPath) {
    res.json('Category with id: ' + catId + 'not found.');
    return;
  }

  snippets = jf.readFileSync(dataPath);
  snippets = snippets.filter(helpers.filterOutDeleted);

  res.json(snippets);
});


router.get('/:id', function(req, res) {
  var config = req.app.get('styleguideConfig'),
    uniques = jf.readFileSync(config.uniques),
    id = Number(req.params.id),
    snippets,
    dataPath,
    desireableSnippet,
    categories = JSON.parse(fs.readFileSync(config.categories));

  if (uniques.indexOf(id) === -1) {
    res.json(false);
    return;
  }

  for (var i = 0, length = categories.length; i < length; i++) {
    dataPath = path.join(config.database, categories[i].name + config.extension);
    snippets = jf.readFileSync(dataPath);

    desireableSnippet = snippets.filter(helpers.filterOutById, id)[0];

    if (desireableSnippet) {
      desireableSnippet.category = i;
      break;
    }
  }

  res.json(desireableSnippet);
});


router.post('/', function(req, res) {
  var config = req.app.get('styleguideConfig'),
    uniques = jf.readFileSync(config.uniques),
    dataPath,
    dataStore,
    id,
    newSnippet,
    index,
    categories = JSON.parse(fs.readFileSync(config.categories)),
    length = categories.length;

  for (index = 0; index < length; index++) {
    if (categories[index].id === Number(req.body.category)) {
      dataPath = path.join(config.database, categories[index].name + config.extension);
    }
  }

  if (!dataPath) {
    res.json('Category with id: ' + req.body.category + ' not found.');
    return;
  }

  dataStore = jf.readFileSync(dataPath);

  function sortNumber(a, b) {
    return a - b;
  }

  uniques.sort(sortNumber);

  if (uniques.length) {
    id = uniques[uniques.length - 1] + 1;
  } else {
    id = 1;
  }

  newSnippet = {
    id: id,
    name: req.body.name,
    code: req.body.code,
    description: req.body.description,
    inlineCss: req.body.inlineCss,
    includeJs: req.body.includeJs,
    isEdited: false,
    isDeleted: false
  };

  dataStore.push(newSnippet);
  uniques.push(id);

  jf.writeFileSync(dataPath, dataStore);
  jf.writeFileSync(config.uniques, uniques);

