        database: path.join('./', folder, 'db'),
        categories: path.join('./', folder, 'db', 'categories.txt'),
        uniques: path.join('./', folder, 'db', 'uniques.txt'),
        sassData: path.join('./', folder, 'db', 'sassdata.txt'),
        extension: '.txt'
