            outputFolder: './templates/scss/icons'
        }));
});

gulp.task('icons', ['sassvg'], function(callback) {
    return gulp.start('sass');
});

gulp.task('watch', function () {
    gulp.watch(['./templates/scss/**/*.scss', '!./templates/scss/icons/*.scss'], ['sass']);
});

