    };

    var openDropdown = function () {
        var trigger = $('.js-open-dropdown'),
            dropdown = $('.js-dropdown-list');

        trigger.on('click', function () {
            var self = $(this);

            function eventOff () {
                self
                    .removeClass('active')
                    .next(dropdown)
                    .removeClass('active');
                $(window).off('click.notButton');
            }

            if (!self.hasClass('active')) {
                self
                    .addClass('active')
                    .next(dropdown)
                    .addClass('active');
                setTimeout(function () {
                    $(window).on('click.notButton', function () {
                        eventOff();
                    });
                }, 5);
            } else {
                eventOff();
            }
        });
    };

